--
-- 表的结构 `tw_comment`
--

CREATE TABLE IF NOT EXISTS `tw_comment` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `content` text COLLATE utf8_unicode_ci NOT NULL,
  `create_time` int(11) DEFAULT NULL,
  `article_id` int(11) NOT NULL,
  `comment_userid` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=8 ;

--
-- 转存表中的数据 `tw_comment`
--

INSERT INTO `tw_comment` (`id`, `content`, `create_time`, `article_id`, `comment_userid`) VALUES
(5, '很漂亮的图片。。。喜欢ing！！！', 1385799364, 30, 273),
(6, '嘻嘻，我自己发表的图图   赞个！！', 1385799435, 30, 275),
(7, '我们都渴望有一个贼铁的朋友，可是那么多时候，都忽略了，某个人把自己当成那个贼铁的朋友！！', 1385799750, 29, 273);

