--
-- 表的结构 `tw_follow`
--

CREATE TABLE IF NOT EXISTS `tw_follow` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `uid` int(10) unsigned NOT NULL COMMENT '谁',
  `touid` int(10) unsigned NOT NULL COMMENT '关注他',
