INSERT INTO `tw_weiyu` (`id`, `weiyu_content`, `author_name`, `create_time`, `update_time`, `create_user_id`, `weiyu_zan`) VALUES
(16, '有些事现在不做，一辈子都不会做了。——《搭车去柏林》', '雪雪雪雪雪', 1385538215, 1385538215, 273, 0),
(17, ' 明天是什么模样？我不知道！我只想好好的过好今天!', '雪雪雪雪雪', 1385538387, 1385538387, 273, 0),
(18, '鸟儿在暴风雨后歌唱，人们为什么在仍是阳光普照的时候还不尽情感受快乐呢？', '雪雪雪雪雪', 1385539156, 1385539156, 273, 0),
(19, '事实上在很多情况下，幸福感是比较出来的，因此重要的不是当前的情况有多么美好，而是事情正在向着好的方向进行着，所以起点低的人才更容易拥有满足感!', '雪雪雪雪雪', 1385541427, 1385541427, 273, 0),
(20, '既见君子，云胡不喜。', '雪雪雪雪雪', 1385541488, 1385541488, 273, 0),
(21, '总有那么一天会去看看看看我的前生今世！！！', '强强', 1385544325, 1385544325, 274, 0),
(22, '有些波折，不必太多；有些遗憾，不必经历。', '强强', 1385544417, 1385544417, 274, 0),
(23, '毕业季，难免会有些忧伤！！！Anyway，Bonjour！！', '小鬼', 1385544893, 1385544893, 261, 0),
(24, '今天是星期三，时间过的好快啊！！', '小鬼', 1385545654, 1385545654, 261, 0),
(25, '我是渠开源的测试微语！！！！', '渠开源', 1385546224, 1385546224, 262, 0),
(26, '今天下午参加奥克斯招聘，加油！', '雪雪雪雪雪', 1385613763, 1385655244, 273, 1),
(27, '为什么会出现这样的问题呢~！！！！！！！！！！！！！', '四只奥特曼', 1385631753, 1385655248, 263, 1),
(28, '心怦怦的跳。。。测试下！！！', '四只奥特曼', 1385633905, 1385655249, 263, 2),
(29, '馨馨新，这里是属于自己心情的地方！', '馨馨新', 1385635241, 1385692313, 275, 1),
